sourcesContent:
import { MongoClient } from 'mongodb';

/**
 * Represents a MongoDB client.
 */
class MongoDBClient {
  /**
   * Creates a new MongoDBClient instance.
   */
  constructor() {
    const mongoHost = process.env.DB_HOST || 'localhost';
    const mongoPort = process.env.DB_PORT || 27017;
    const mongoDatabase = process.env.DB_DATABASE || 'files_manager';
    const mongoURL = `mongodb://${mongoHost}:${mongoPort}/${mongoDatabase}`;

    // Initialize the MongoClient
    this.mongoClient = new MongoClient(mongoURL, { useUnifiedTopology: true });
    this.connectionStatus = false;

    // Connect to MongoDB
    this.mongoClient.connect((connectionError) => {
      if (!connectionError) {
        this.connectionStatus = true;
      } else {
        console.error('Failed to connect to MongoDB:', connectionError.message);
      }
    });
  }

  /**
   * Checks if this client's connection to the MongoDB server is active.
   * @returns {boolean}
   */
  isConnectionAlive() {
    return this.connectionStatus;
  }

  /**
   * Retrieves the number of users in the database.
   * @returns {Promise<Number>}
   */
  async countUsers() {
    if (!this.isConnectionAlive()) return 0;
    return this.mongoClient.db().collection('users').countDocuments();
  }

  /**
   * Retrieves the number of files in the database.
   * @returns {Promise<Number>}
   */
  async countFiles() {
    if (!this.isConnectionAlive()) return 0;
    return this.mongoClient.db().collection('files').countDocuments();
  }
}

// Create and export an instance of MongoDBClient
const mongoDBClient = new MongoDBClient();
export default mongoDBClient;

